INSERT INTO champions (name, role, lore, image_url) VALUES
    ('Aatrox', 'Lutador', 'Aatrox é um guerreiro lendário, um dos últimos de sua raça. Ele aniquilou exércitos inteiros para alimentar seu desejo insaciável de batalha e sangue. Sua espada gigante é uma extensão de seu próprio ser, consumindo almas e despedaçando a vontade dos que se opõem a ele.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Aatrox_0.jpg'),
    ('Ahri', 'Mago', 'Ahri é uma raposa de nove caudas com o poder de encantar suas presas e absorver suas essências vitais. Ela vagueia pelo mundo em busca de emoções humanas, sempre buscando uma maneira de se tornar humana novamente. Sua beleza é tão sedutora quanto sua natureza predatória.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Ahri_0.jpg'),
    ('Akali', 'Assassino', 'Akali é uma mestra assassina e ninja, treinada desde a infância para se tornar uma arma mortal. Ela usa sua agilidade e habilidade com as sombras para se movimentar pelo campo de batalha, eliminando seus alvos sem deixar rastro. Sua determinação e habilidade são incomparáveis, tornando-a uma adversária temida por todos.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Akali_0.jpg'),
    ('Alistar', 'Tanque', 'Alistar é um minotauro poderoso que usa sua força bruta para proteger seus aliados e esmagar seus inimigos. Ele é conhecido por sua determinação inabalável e lealdade aos fracos. Mesmo nas batalhas mais desesperadas, Alistar nunca recua, defendendo seus amigos com todas as suas forças.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Alistar_0.jpg'),
    ('Amumu', 'Tanque', 'Amumu é uma múmia solitária que busca desesperadamente por amizade e afeto. Ele foi amaldiçoado a vagar pelo mundo sem nunca encontrar paz ou companhia. Apesar de sua aparência assustadora, Amumu tem um coração gentil e anseia por encontrar alguém que o aceite como ele é.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Amumu_0.jpg'),
    ('Anivia', 'Mago', 'Anivia é um espírito lendário que personifica o gelo e o frio. Ela protege os Freljord das forças da escuridão e da destruição, usando sua magia congelante para deter qualquer ameaça. Apesar de sua aparência majestosa, Anivia é uma guardiã feroz que não hesita em lutar até o fim pela sua terra natal.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Anivia_0.jpg'),
    ('Annie', 'Mago', 'Annie é uma menina prodígio com o poder de controlar o fogo. Ela usa sua habilidade mágica para causar destruição e caos onde quer que vá. Apesar de sua aparência inocente, Annie é uma adversária perigosa que não deve ser subestimada.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Annie_0.jpg'),
    ('Aphelios', 'Atirador', 'Aphelios é um assassino silencioso que caça suas presas sob a luz da lua. Ele usa um arsenal de armas mágicas para eliminar seus alvos com precisão mortal. Sua origem misteriosa e habilidades letais o tornam um oponente temido por todos.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Aphelios_0.jpg'),
    ('Ashe', 'Atirador', 'Ashe é a rainha de gelo de Freljord, uma líder poderosa e uma guerreira habilidosa. Ela usa seu arco e flecha para defender seu povo e proteger sua terra natal de qualquer ameaça. Sua coragem e determinação inspiram seus aliados e amedrontam seus inimigos.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Ashe_0.jpg'),
    ('Aurelion Sol', 'Mago', 'Aurelion Sol é um dragão celestial que cria estrelas e galáxias com seu sopro divino. Ele é uma força cósmica que transcende os limites do universo, manipulando o espaço e o tempo ao seu bel-prazer. Apesar de sua imensa poder, Aurelion Sol é uma criatura solitária que busca compreensão e propósito em um mundo vasto e incompreensível.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/AurelionSol_0.jpg'),
    ('Azir', 'Mago', 'Azir é o imperador ressuscitado de Shurima, um líder poderoso e um guerreiro implacável. Ele usa seu controle sobre a areia e o sol para conquistar territórios e subjugar seus inimigos. Sua determinação e ambição são incomparáveis, tornando-o uma força a ser reconhecida em todo o mundo.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Azir_0.jpg'),
    ('Bard', 'Suporte', 'Bard é um guardião místico que viaja pelos reinos, protegendo o equilíbrio do universo. Ele usa sua magia ancestral para curar os feridos e proteger os indefesos. Sua sabedoria e compaixão o tornam uma figura venerada em todos os lugares que ele visita.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Bard_0.jpg'),
    ('Blitzcrank', 'Tanque', 'Blitzcrank é um golem gigante equipado com poderosas habilidades mecânicas. Ele usa sua força e resistência para proteger seus aliados e eliminar seus inimigos. Sua determinação e devoção aos ideais de justiça o tornam uma força imparável em batalha.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Blitzcrank_0.jpg'),
    ('Brand', 'Mago', 'Brand é um espírito flamejante que busca vingança contra aqueles que o prenderam em uma prisão de gelo por séculos. Ele usa seu poder elemental para queimar tudo em seu caminho, deixando apenas cinzas e destruição para trás. Sua fúria ardente é uma força da natureza que não pode ser contida.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Brand_0.jpg'),
    ('Braum', 'Tanque', 'Braum é um herói lendário que protege os fracos e defende os inocentes com sua força e coragem. Ele usa seu escudo impenetrável para bloquear ataques e proteger seus aliados de qualquer perigo. Sua gentileza e generosidade são tão lendárias quanto sua força, tornando-o uma figura amada por todos.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Braum_0.jpg'),
    ('Caitlyn', 'Atirador', 'Conhecida como sua melhor pacificadora, Caitlyn também é a melhor escolha de Piltover para livrar a cidade de seus elementos criminosos elusivos. É comum que ela faça dupla com Vi, agindo como um contraponto de calmaria para a natureza impetuosa de sua parceira. Mesmo carregando um rifle hextec único, a arma mais poderosa de Caitlyn é seu intelecto superior, que permite que ela prepare elaboradas armadilhas para qualquer fora da lei tolo o suficiente para operar na Cidade do Progresso.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Caitlyn_0.jpg'),
    ('Camille', 'Lutador', 'Camille é uma engenheira brilhante que utiliza seu corpo modificado e suas habilidades mecânicas para eliminar seus inimigos. Ela é uma mestra da agilidade e da precisão, capaz de derrotar até os oponentes mais poderosos com facilidade. Sua determinação e inteligência a tornam uma adversária temível em qualquer campo de batalha.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Camille_0.jpg'),
    ('Cassiopeia', 'Mago', 'Cassiopeia é uma caçadora de tesouros ambiciosa que foi amaldiçoada por sua ganância. Ela é uma criatura metade mulher, metade serpente, cujo veneno mortal pode derrubar até mesmo os mais poderosos guerreiros. Sua sede de poder é insaciável, levando-a a buscar relíquias antigas e artefatos mágicos a qualquer custo.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Cassiopeia_0.jpg'),
    ('ChoGath', 'Tanque', 'ChoGath é um monstro voraz que devora tudo em seu caminho. Ele é uma criatura antiga e poderosa, cuja fome insaciável o leva a consumir até mesmo os seres mais poderosos. Sua presença é uma ameaça constante para todos que cruzam seu caminho.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Chogath_0.jpg'),
    ('Corki', 'Atirador', 'Corki é um piloto habilidoso que voa pelo céu em sua aeronave de combate. Ele é um mestre do ar e do fogo, capaz de eliminar seus inimigos com seus ataques precisos e explosivos. Sua bravura e destreza o tornam uma força a ser reconhecida em qualquer batalha aérea.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Corki_0.jpg'),
    ('Darius', 'Lutador', 'Darius é um guerreiro temido que comanda o campo de batalha com sua força bruta e sua habilidade com a espada. Ele é um líder implacável que inspira seus aliados e aterroriza seus inimigos com sua presença imponente. Sua determinação e coragem o tornam uma figura lendária em Noxus e além.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Darius_0.jpg'),
    ('Diana', 'Assassino', 'Diana é uma guerreira solitária que busca vingança contra os deuses que a baniram. Ela é uma mestra da lua e da escuridão, capaz de eliminar seus inimigos com sua habilidade e astúcia. Sua determinação e poder são incomparáveis, tornando-a uma adversária temível para qualquer um que se atreva a desafiá-la.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Diana_0.jpg'),
    ('DrMundo', 'Tanque', 'Dr. Mundo é um cientista louco que se transformou em uma máquina de matar imparável. Ele é uma força da natureza, capaz de resistir a qualquer dano e regenerar-se rapidamente. Sua insanidade e ferocidade o tornam uma ameaça constante para todos que cruzam seu caminho.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/DrMundo_0.jpg'),
    ('Draven', 'Atirador', 'Draven é um gladiador arrogante que adora estar no centro das atenções. Ele é um mestre do arremesso de machados, capaz de eliminar seus inimigos com sua habilidade e precisão. Sua arrogância e vaidade o tornam uma figura polarizadora, amada por alguns e odiada por outros.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Draven_0.jpg'),
    ('Ekko', 'Assassino', 'Um prodígio das ruas violentas de Zaun, Ekko manipula o tempo para reverter qualquer situação a seu favor. Usando sua própria invenção, o Revo-Z, ele explora as possíveis bifurcações da realidade para criar o momento perfeito. Embora valorize muito sua liberdade, quando algo ameaça seus amigos, ele não mede esforços para defendê-los. Para meros observadores, Ekko parece conseguir o impossível sempre de primeira.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Ekko_0.jpg'),
    ('Elise', 'Mago', 'Elise é uma sacerdotisa sinistra que se tornou uma criatura monstruosa em busca de poder. Ela usa sua magia negra para manipular os vivos e os mortos, criando um exército de aranhas para fazer sua vontade. Sua ambição e crueldade são inigualáveis, tornando-a uma inimiga terrível para todos que cruzam seu caminho.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Elise_0.jpg'),
    ('Evelynn', 'Assassino', 'Evelynn é um demônio sedutor que se alimenta da dor e do sofrimento dos outros. Ela usa sua beleza e charme para atrair suas presas antes de revelar sua verdadeira forma e devorá-las viva. Sua crueldade e malícia são incomparáveis, tornando-a uma ameaça constante para todos que se atrevem a cruzar seu caminho.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Evelynn_0.jpg'),
    ('Ezreal', 'Atirador', 'Ezreal é um explorador aventureiro que viaja pelo mundo em busca de artefatos mágicos perdidos. Ele é um mestre das artes arcanas, capaz de manipular a energia mágica para derrotar seus inimigos. Sua curiosidade e determinação o levam a grandes aventuras, onde ele descobre segredos antigos e enfrenta perigos inimagináveis.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Ezreal_0.jpg'),
    ('Fiddlesticks', 'Mago', 'Fiddlesticks é um espantalho assustador que assombra os campos de batalha com sua presença macabra. Ele é uma criatura antiga e malévola, cujo único propósito é semear o medo e o terror onde quer que vá. Sua aparência sinistra e suas habilidades mágicas o tornam uma ameaça constante para todos que ousam cruzar seu caminho.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Fiddlesticks_0.jpg'),
    ('Fiora', 'Lutador', 'Fiora é uma espadachim habilidosa que busca se tornar a melhor guerreira do mundo. Ela é uma mestra da esgrima, capaz de derrotar seus inimigos com graça e precisão. Sua determinação e ambição são incomparáveis, tornando-a uma adversária temível em qualquer duelo.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Fiora_0.jpg'),
    ('Fizz', 'Assassino', 'Fizz é um yordle travesso que se diverte nas profundezas do oceano. Ele é um mestre do mar, capaz de manipular a água e controlar os peixes ao seu redor. Sua astúcia e agilidade o tornam um adversário formidável, capaz de derrotar até os inimigos mais poderosos com facilidade.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Fizz_0.jpg'),
    ('Galio', 'Tanque', 'Galio é um golem gigante que protege Demacia de qualquer ameaça. Ele é uma criatura imponente, cuja força e resistência são incomparáveis. Sua lealdade a Demacia é inabalável, e ele está disposto a sacrificar tudo para defender sua terra natal.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Galio_0.jpg'),
    ('Gangplank', 'Lutador', 'Gangplank é um pirata impiedoso que governa os mares com punho de ferro. Ele é um mestre do saque e do pilhagem, capaz de saquear cidades inteiras em questão de horas. Sua ambição e crueldade são incomparáveis, tornando-o uma lenda entre os piratas de Bilgewater.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Gangplank_0.jpg'),
    ('Garen', 'Lutador', 'Garen é um guerreiro destemido que luta pela justiça e pela honra. Ele é um mestre da espada, capaz de derrotar seus inimigos com força e precisão. Sua coragem e determinação são incomparáveis, tornando-o uma figura lendária em Demacia e além.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Garen_0.jpg'),
    ('Gnar', 'Lutador', 'Gnar é um yordle selvagem que se transforma em uma fera feroz em batalha. Ele é um mestre da agilidade e da velocidade, capaz de derrotar seus inimigos com ataques rápidos e precisos. Sua natureza indomável o torna uma ameaça constante para qualquer um que se atreva a desafiá-lo.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Gnar_0.jpg'),
    ('Gragas', 'Tanque', 'Gragas é um alquimista bêbado que usa sua habilidade com as poções para criar bebidas poderosas e explosivas. Ele é um mestre da festa, capaz de beber qualquer um sob a mesa e deixar seus inimigos inconscientes com uma única explosão. Sua bravura e destemor são incomparáveis, tornando-o uma lenda entre os habitantes de Runeterra.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Gragas_0.jpg'),
    ('Graves', 'Atirador', 'Graves é um pistoleiro habilidoso que luta pelo seu próprio código de honra. Ele é um mestre das armas de fogo, capaz de derrotar seus inimigos com precisão e velocidade. Sua determinação e coragem são incomparáveis, tornando-o uma lenda entre os caçadores de recompensas de toda a terra.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Graves_0.jpg'),
    ('Gwen', 'Lutador', 'Gwen foi uma costureira que perdeu a vida de maneira trágica, mas foi trazida de volta à vida por Hecarim, o Cavaleiro das Trevas. Sua nova existência é marcada por uma sede de vingança contra aqueles que a traíram, ao mesmo tempo que procura descobrir seu lugar no mundo.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Gwen_0.jpg'),
    ('Hecarim', 'Lutador', 'Hecarim é um cavaleiro espectral que cavalga pelo campo de batalha com velocidade e ferocidade implacáveis. Ele é um mestre da morte, capaz de trazer a ruína para qualquer um que ouse cruzar seu caminho. Sua sede de sangue é insaciável, tornando-o uma ameaça constante para todos os vivos.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Hecarim_0.jpg'),
    ('Heimerdinger', 'Mago', 'Heimerdinger é um inventor brilhante que cria máquinas e dispositivos incríveis para ajudar seus aliados e frustrar seus inimigos. Ele é um mestre da ciência, capaz de transformar até mesmo os conceitos mais abstratos em realidade tangível. Sua inteligência e criatividade são incomparáveis, tornando-o uma figura lendária em Piltover e Zaun.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Heimerdinger_0.jpg'),
    ('Illaoi', 'Lutador', 'Illaoi é uma sacerdotisa guerreira que serve Nagakabouros, o Deus Serpente. Ela é uma mestra do combate corpo a corpo, capaz de esmagar seus inimigos com sua força bruta e sua conexão com os espíritos ancestrais. Sua devoção e determinação são incomparáveis, tornando-a uma adversária temível em qualquer campo de batalha.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Illaoi_0.jpg'),
    ('Irelia', 'Lutador', 'Irelia é uma dançarina guerreira que usa suas lâminas flutuantes para derrotar seus inimigos com graça e estilo. Ela é uma mestra da agilidade e da precisão, capaz de desviar de ataques e contra-atacar com rapidez e eficiência. Sua determinação e coragem são incomparáveis, tornando-a uma adversária temível em qualquer duelo.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Irelia_0.jpg'),
    ('Ivern', 'Tanque', 'Ivern é um espírito da floresta que protege a natureza com sua vida. Ele é um guardião gentil, capaz de curar feridas e reviver a vida onde quer que vá. Sua devoção à vida e ao meio ambiente é incomparável, tornando-o uma figura venerada entre todos os seres vivos.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Ivern_0.jpg'),
    ('Janna', 'Suporte', 'Janna é uma sacerdotisa do vento que protege os inocentes com sua magia. Ela é uma mestra dos elementos, capaz de controlar os ventos e as tempestades para derrotar seus inimigos. Sua gentileza e compaixão são incomparáveis, tornando-a uma figura venerada em todas as terras.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Janna_0.jpg'),
    ('Jarvan IV', 'Lutador', 'Jarvan IV é um príncipe guerreiro que luta pelo seu povo com honra e coragem. Ele é um mestre da estratégia e da liderança, capaz de inspirar seus aliados e aterrorizar seus inimigos com sua presença imponente. Sua determinação e lealdade são incomparáveis, tornando-o uma figura lendária em Demacia e além.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/JarvanIV_0.jpg'),
    ('Jax', 'Lutador', 'Jax é um guerreiro misterioso que luta com uma arma improvisada. Ele é um mestre do combate corpo a corpo, capaz de derrotar seus inimigos com força e habilidade. Sua determinação e habilidade são incomparáveis, tornando-o uma lenda entre os combatentes de toda a terra.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Jax_0.jpg'),
    ('Jayce', 'Lutador', 'Jayce é um inventor brilhante que cria armas poderosas para proteger sua cidade natal de qualquer ameaça. Ele é um mestre da tecnologia, capaz de transformar até mesmo os conceitos mais abstratos em realidade tangível. Sua inteligência e coragem são incomparáveis, tornando-o uma figura lendária em Piltover e Zaun.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Jayce_0.jpg'),
    ('Jhin', 'Atirador', 'Jhin é um assassino sádico que se deleita em infligir dor e sofrimento aos outros. Ele é um mestre do tiro de precisão, capaz de eliminar seus alvos com um único disparo. Sua obsessão pela perfeição e pela estética o tornam uma figura sinistra e temível, capaz de semear o medo onde quer que vá.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Jhin_0.jpg'),
    ('Jinx', 'Atirador', 'Jinx é uma maníaca homicida que adora causar o caos e a destruição onde quer que vá. Ela é uma mestra do explosivo, capaz de eliminar seus inimigos com uma combinação mortal de armas e habilidades. Sua loucura e imprudência são incomparáveis, tornando-a uma ameaça constante para todos que cruzam seu caminho.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Jinx_0.jpg'),
    ('Kaisa', 'Atirador', 'Kaisa é uma sobrevivente determinada que busca vingança contra as criaturas que a sequestraram quando criança. Ela é uma mestra da caça, capaz de eliminar seus inimigos com precisão e habilidade. Sua determinação e coragem são incomparáveis, tornando-a uma adversária temível em qualquer batalha.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Kaisa_0.jpg'),
    ('Kalista', 'Atirador', 'O espectro da vingança, Kalista é um pesadelo para traidores e perjuros. Uma vez uma honrada sentinela da justiça, ela foi traída por aqueles a quem jurou proteger. Agora ela é um arauto da morte, assombrando os que a traíram e arrastando as almas dos culpados para o abismo.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Kalista_0.jpg'),
    ('Karma', 'Mago', 'Karma é uma líder espiritual cuja sabedoria guia os povos de Ionia. Ela é dotada de um poder espiritual imenso, capaz de dobrar a vontade dos outros e proteger os inocentes. Com sua bondade e compaixão, ela busca trazer equilíbrio e harmonia ao seu povo.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Karma_0.jpg'),
    ('Karthus', 'Mago', 'Karthus é um lich imortal que busca trazer a morte e a destruição ao mundo dos vivos. Ele é um mestre da necromancia, capaz de comandar exércitos inteiros de mortos-vivos para fazer sua vontade. Sua ambição e crueldade são incomparáveis, tornando-o uma ameaça constante para todos que cruzam seu caminho.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Karthus_0.jpg'),
    ('Kassadin', 'Assassino', 'Kassadin é um caçador de magos que busca vingança contra os seres que habitam o Vazio. Ele é um mestre do vazio, capaz de absorver a energia mágica ao seu redor e usar seu poder para destruir seus inimigos. Sua determinação e coragem são incomparáveis, tornando-o uma ameaça constante para todos que praticam a magia.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Kassadin_0.jpg'),
    ('Katarina', 'Assassino', 'Katarina é uma assassina mortal que busca vingança contra os inimigos de Noxus. Ela é uma mestra da agilidade e da precisão, capaz de eliminar seus alvos com graça e eficiência. Sua determinação e crueldade são incomparáveis, tornando-a uma figura lendária entre os assassinos de Noxus.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Katarina_0.jpg'),
    ('Kayle', 'Lutador', 'Kayle é uma guerreira celestial que luta pelo bem e pela justiça. Ela é uma mestra da espada, capaz de derrotar seus inimigos com força e habilidade divinas. Sua determinação e coragem são incomparáveis, tornando-a uma figura venerada entre os povos de Runeterra.', 'https://ddragon.leagueoflegends.com/cdn/img/champion/splash/Kayle_0.jpg'),
